import React, { useState } from "react";
import { useSelector } from "react-redux";
import BookModal from "../BookModal/BookModal";

const Content = () => {
  const booksArr = useSelector((state) => state.booksReducer.books);
  const [selectedBook, setSelectedBook] = useState({});
  const [isOpen, setIsOpen] = useState(false);

  const openModal = (book) => {
    setSelectedBook(book);
    setIsOpen(true);
  };
  return (
    <div className="m-auto w-11/12 mt-5 min-h-[55vh] flex">
      <BookModal
        book={selectedBook}
        isOpen={isOpen}
        onClose={() => setIsOpen(false)}
      />
      {booksArr && booksArr.length > 0 ? (
        booksArr.map((book) => {
          return (
            book.volumeInfo.imageLinks &&
            book.volumeInfo.authors && (
              <div
                className="w-80 m-5 inline-block text-center bg-slate-200 p-3 rounded"
                key={book.id}
              >
                <img
                  className="w-11/12 h-52 ml-auto mr-auto object-contain pb-4"
                  alt="Ä±magesLive"
                  src={book.volumeInfo.imageLinks.smallThumbnail}
                />
                <h5 className="mt-4 text-base">{book.volumeInfo.title}</h5>
                <h6 className="mt-0">{book.volumeInfo.authors[0]}</h6>
                <br />
                <Button
                  href={book.volumeInfo.previewLink}
                  target="_blank"
                  className={Styles.textleft}
                >
                  Preview
                </Button>
                <Button
                  onClick={() => openModal(book)}
                  className={Styles.textright}
                >
                  Details
                </Button>
              </div>
            )
          );
        })
      ) : (
        <NothingToShow>
          <div>
            Nothing <br />
            To
            <br />
            Show!?
            <br />
          </div>
        </NothingToShow>
      )}
    </div>
  );
};

export default Content;
