import React from "react";
import { GrClose } from "react-icons/gr";

const BookModal = ({ book, isOpen, onClose }) => {
  if (!isOpen) return null;
  return (
    <>
      <div
        className="z-[999] fixed top-0 left-0 right-0 bottom-0 bg-slate-200"
        onClick={onClose}
      />
      <div className="fixed top-2/4 left-2/4 bg-white p-12 z-[999] w-2/5 rounded">
        <img src={book.volumeInfo.imageLinks.smallThumbnail} alt="images" />
        <GrClose />
        <div>
          <h4>{book.volumeInfo.title}</h4>
          <p>({book.volumeInfo.publishedDate})</p>
          {book.volumeInfo.authors.map((a, i) => (
            <h5 key={i}>{a}</h5>
          ))}
          <p>{book.volumeInfo.description}</p>
        </div>
      </div>
    </>
  );
};

export default BookModal;
